    return(
        <div>
      <div>
        <div>
          <div>
            <span>
              <p >
                <span>GameVerse</span>
              </p>
            </span>
            <p c>
              "Dive into the world of Gamers"
            </p>
          </div>
        </div>

        <div >
          <p >Login</p>
          <input
            type='text'
            placeholder='Email'
            name='email'
            id='email'
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <div className='relative w-full max-w-xs'>
            <input
              type={view ? `text` : `password`}
              placeholder='Password'
              name='password'
              id='password'
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
            <VisibilityIcon
              onClick={(e) => setView(!view)}
              style={{
                cursor: "pointer",
                position: "absolute",
                right: "10px",
                top: "50%",
                transform: "translateY(-50%)",
              }}
            />
          </div>
          <button
            onClick={handleLogin}
          >
            Log In
          </button>
          <Link to={"/register"}>
            <p>Don't have an account?</p>
          </Link>
        </div>
      </div>
    </div>
    );
};

export default Login;
